const int NUM_WAVES = 5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

   	float wave1;

    st=5.0*st-2.0;
    float moving_sun = 2.5*abs(sin(0.4*iTime));
    float dist_sun = (distance(st,vec2(0.,moving_sun)));
    dist_sun = 1.0-abs(cos(.4*iTime))*(dist_sun/5.0)-.2*abs(cos(.4*iTime));
    vec3 colour= vec3(dist_sun);
    //vec3 colour= vec3(1.0-(0.2*abs(st.y))*0.2*(abs(st.x)));

    vec3 grey=vec3(.5);
    for(int j = NUM_WAVES; j >0; j--)
    {
        float i = float(j);
    	float wave_input= .8/(2.5*i)*(sin(+iTime))*(sin(0.5*i*st.x*5.+iTime*2.))-.8/i-1.+1.;
    	wave1 = step((wave_input),st.y);
        colour.b = mix((.15*i)*(1.-0.5*abs(cos(.4*iTime))),colour.b,wave1);

    }
    colour = vec3(colour.b);

    fragColor = vec4(colour,1.0);
}
